import React from 'react';
import { useState } from 'react';
import {Route, BrowserRouter as Router, Switch, Link } from "react-router-dom";
import Home from './Pages/Home/Home';
import About from './Pages/About/About';
import Portfolio from './Pages/Portfolio/Portfolio';
import Poetry from './Pages/Poetry/Poetry';
import Contact from './Pages/Contact/Contact';
import './App.scss';
import Logo from './assets/LOGO.svg';

function App() {
  const [path, setPath] = useState(window.location.pathname);

  return (
    <Router>
      <div class="container">
        <div class="logo">
          <img class="logo-img" src={Logo} alt="Kaili Ordiz Logo"/>
        </div>
        <div class="nav-bar">
          <div class="nav-item">
            <Link to="/" onClick={() => setPath(window.location.pathname)}>home</Link>
          </div>
          <div class="nav-item">
            <Link to="/about" onClick={() => setPath(window.location.pathname)}>about</Link>
          </div>
          <div class="nav-item">
            <Link to="/portfolio" onClick={() => setPath(window.location.pathname)}>portfolio</Link>
          </div>
          <div class="nav-item" className={path === '/poetry' ? "bold": ""}>
            <Link to="/poetry" onClick={() => setPath(window.location.pathname)}>poetry</Link>
          </div>
          <div class="nav-item">
            <Link to="/contact" onClick={() => setPath(window.location.pathname)}>contact me</Link>
          </div>
        </div>
        <div class="rest">

          <Switch>
            <Route path="/about" component={About} />
            <Route path="/contact" component={Contact} />
            <Route path="/poetry" component={Poetry} />
            <Route path="/portfolio" component={Portfolio} />
            <Route path="/" component={Home} />
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
