import React from 'react';
import { useState } from 'react';
import {Route, BrowserRouter as Router, Switch, Link } from "react-router-dom";
import './Portfolio.scss';
import Photography from './PortfolioPages/Photography/Photography.js';
import Painting from './PortfolioPages/Painting/Painting.js';
import Animation from './PortfolioPages/Animation/Animation.js';
import vector from '../../assets/Vector.svg';


export default function Portfolio() {

    const [path, setPath] = useState(window.location.pathname);
    console.log(path);
    return (
        <Router>
            <div class="portfolio-container">

                <div class="overlay">

                    <div class="overlay-background">
                        <img class="overlay-background-img" src={vector} alt="Vector"/>
                    </div>
                    <div class="portfolio-nav-bar">
                        <div className={path === '/portfolio/animation' ? "bold portfolio-nav-item1" : "portfolio-nav-item1"}>
                        <Link to="portfolio/animation" onClick={() => setPath("/portfolio/animation")}>animation</Link>
                        </div>
                        <div className={path === '/portfolio/painting' ? "bold portfolio-nav-item2" : "portfolio-nav-item2"}>
                        <Link to="portfolio/painting" onClick={() => setPath("/portfolio/painting")}>painting</Link>
                        </div>
                        <div className={path === '/portfolio/photography' ? "bold portfolio-nav-item3" : "portfolio-nav-item3"}>
                        <Link to="portfolio/photography" onClick={() => setPath("/portfolio/photography")}>photography</Link>
                        </div>

                    </div>
                    <div class="boxes">

                        <Switch>
                            <Route path="/portfolio/animation" component={Animation} />
                            <Route path="/portfolio/painting" component={Painting} />
                            <Route path="/portfolio/photography" component={Photography} />
                        </Switch>

                    </div>
                </div>
            </div>
        </Router>
    );
}
